CREATE TABLE `dqlinh_reportazmedia`.`user_month_settings` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `month` VARCHAR(255) NOT NULL , `year` VARCHAR(255) NOT NULL , `day_off` INT(11) NOT NULL , `time_required` VARCHAR(255) NOT NULL , `status` INT(11) NOT NULL DEFAULT '0' , `created` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;